package pk03;

public class Sample6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("10 진수 10 은 " + 10 +"입니다.");
		System.out.println("8진수 10 은 " + 010 + "입니다.");
		System.out.println("16진수 10은 " + 0x10 +" 입니다");
		System.out.println("16진수 F 는 "+ 0xF + "입니다.");
		
		System.out.println("2진수 20은 " + Integer.toBinaryString(20) + "입니다.");
		System.out.println("8진수 20은 " + Integer.toOctalString(20) + "입니다.");
		System.out.println("16진수 20은 " + Integer.toHexString(20) + "입니다.");
		
		
	}

}
